import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './PieceList.css';

function PieceList({ userId }) {
  const [pieces, setPieces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [searchBy, setSearchBy] = useState('title'); // 'title' or 'composer'
  const [filteredPieces, setFilteredPieces] = useState([]);
  const [searching, setSearching] = useState(false);

  useEffect(() => {
    // Initial load of local pieces
    fetch('http://localhost:4000/api/pieces')
      .then(res => res.json())
      .then(data => {
        setPieces(data);
        setFilteredPieces(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching pieces:', error);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    const searchPieces = async () => {
      if (!searchTerm.trim()) {
        setFilteredPieces(pieces);
        return;
      }

      setSearching(true);
      try {
        const response = await fetch(
          `http://localhost:4000/api/pieces/search?query=${encodeURIComponent(searchTerm)}&type=${searchBy}`
        );
        const data = await response.json();
        setFilteredPieces(data);
      } catch (error) {
        console.error('Search error:', error);
      } finally {
        setSearching(false);
      }
    };

    const debounceTimeout = setTimeout(searchPieces, 300);
    return () => clearTimeout(debounceTimeout);
  }, [searchTerm, searchBy]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearchByChange = (e) => {
    setSearchBy(e.target.value);
    setSearchTerm(''); // Clear search when switching search type
  };

  if (loading) return <div className="pieces-loading">Loading...</div>;

  return (
    <div className="pieces-container">
      <h1>Browse Classical Pieces</h1>

      <div className="search-section">
        <div className="search-controls">
          <div className="search-type">
            <select 
              value={searchBy} 
              onChange={handleSearchByChange}
              className="search-select"
            >
              <option value="title">Search by Title</option>
              <option value="composer">Search by Composer</option>
            </select>
          </div>
          <input
            type="text"
            placeholder={`Search by ${searchBy}...`}
            value={searchTerm}
            onChange={handleSearchChange}
            className="search-input"
          />
        </div>

        {searching && (
          <div className="searching-indicator">
            Searching IMSLP database...
          </div>
        )}

        {!searching && filteredPieces.length === 0 && (
          <p className="no-results">
            No pieces found for "{searchTerm}"
          </p>
        )}
      </div>

      <div className="pieces-grid">
        {filteredPieces.map(piece => (
          <div key={piece.id} className="piece-card">
            <h3>
              <Link to={`/piece/${piece.id}`}>
                {piece.title}
              </Link>
            </h3>
            <p className="composer-name">{piece.composer}</p>
            {piece.difficulty && (
              <p>Difficulty: {piece.difficulty}/10</p>
            )}
            {piece.imslp_id && (
              <span className="imslp-badge">From IMSLP</span>
            )}
            <div className="piece-card-actions">
              <Link to={`/piece/${piece.id}`} className="view-details">
                View Details
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default PieceList;
