import React, { useEffect, useState } from 'react';

function App() {
  const [pieces, setPieces] = useState([]);
  const [selectedPiece, setSelectedPiece] = useState(null);
  const [ratingType, setRatingType] = useState('listening');
  const [ratingValue, setRatingValue] = useState('');

  useEffect(() => {
    fetch('http://localhost:4000/api/pieces')
      .then((res) => res.json())
      .then((data) => setPieces(data))
      .catch((err) => console.error(err));
  }, []);

  const fetchPieceById = (id) => {
    fetch(`http://localhost:4000/api/pieces/${id}`)
      .then((res) => res.json())
      .then((data) => setSelectedPiece(data))
      .catch((err) => console.error(err));
  };

  const handleRate = () => {
    if (!selectedPiece) return;
    fetch(`http://localhost:4000/api/pieces/${selectedPiece.id}/rate`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ ratingType, ratingValue: parseInt(ratingValue, 10) })
    })
      .then((res) => res.json())
      .then((updatedPiece) => {
        setSelectedPiece(updatedPiece);
        // Update the piece list state (optional, for real-time sync)
        setPieces((prevPieces) =>
          prevPieces.map((p) => (p.id === updatedPiece.id ? updatedPiece : p))
        );
      })
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ margin: '2rem' }}>
      <h1>Classical Music MVP</h1>

      {/* Pieces List */}
      <div style={{ display: 'flex', gap: '2rem' }}>
        <div>
          <h2>Pieces</h2>
          <ul>
            {pieces.map((piece) => (
              <li key={piece.id} style={{ marginBottom: '0.5rem' }}>
                <button onClick={() => fetchPieceById(piece.id)}>
                  {piece.title} by {piece.composer}
                </button>
              </li>
            ))}
          </ul>
        </div>

        {/* Selected Piece Details */}
        {selectedPiece && (
          <div style={{ border: '1px solid #ccc', padding: '1rem', width: '300px' }}>
            <h2>Selected Piece</h2>
            <p><strong>Title:</strong> {selectedPiece.title}</p>
            <p><strong>Composer:</strong> {selectedPiece.composer}</p>
            <p><strong>Difficulty:</strong> {selectedPiece.difficulty}</p>

            <h3>Ratings</h3>
            <ul>
              {selectedPiece.ratings.map((r, idx) => (
                <li key={idx}>
                  {r.ratingType} - {r.ratingValue}
                </li>
              ))}
            </ul>

            {/* Rating Form */}
            <div>
              <h4>Add a Rating</h4>
              <label>
                Rating Type:
                <select value={ratingType} onChange={(e) => setRatingType(e.target.value)}>
                  <option value="listening">Listening</option>
                  <option value="playing">Playing</option>
                </select>
              </label>
              <br />
              <label>
                Rating Value (1-10):
                <input
                  type="number"
                  value={ratingValue}
                  onChange={(e) => setRatingValue(e.target.value)}
                  min="1"
                  max="10"
                />
              </label>
              <br />
              <button onClick={handleRate}>Submit Rating</button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
